import { Component, OnInit,Input } from '@angular/core';
import { FormControl,FormBuilder, FormGroup } from '@angular/forms';
import { ServiceService } from 'src/app/service.service';

@Component({
  selector: 'app-track',
  templateUrl: './track.component.html',
  styleUrls: ['./track.component.css'],
  providers:[ServiceService]
})
export class TrackComponent implements OnInit {
  @Input() fromParent!: string;


myForm:FormGroup;
weatherData:any;
cityName!: string;





constructor(private formBuilder: FormBuilder,private service :ServiceService){
  this.myForm = this.formBuilder.group({
  emailid:'',
  city:'',

  });




}
  ngOnInit(): void {

   //  this.SendTemp();


  }

onSubmit():void{
  const formData = this.myForm.value;
  this.cityName=formData.city;

  console.log('Form submitted:',formData);
  console.log(this.cityName);
  this.SendTemp(this.cityName);


  this.myForm.reset();
}

SendTemp(cityName:any):void{

  this.service.getJsonData(cityName).subscribe(data=>{
this.weatherData = Math.round(data.main.temp-273.15) ;


   })




}
}
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Weather Tracker</title>
</head>
<body>
<div>

    <form [formGroup]="myForm" (ngSubmit)="onSubmit()">

    <label for="emailid">Email id: </label>
    <input type="text" id="emailid" formControlName="emailid" placeholder="Enter your email ID">


    <br>

    <br>

    <label for="city" >City: </label>
    <input type="text" id="city" formControlName="city" placeholder="Enter your city you want to track">

    <br>

    <br>
    <button type="submit" >Submit</button>

    </form>


    </div>

    <div>

        <p>Email: {{ this.myForm.controls['emailid'].value}}</p>

        <p>City: {{ this.myForm.controls['city'].value}}</p>

    </div>

    <div>
         Weather is {{this.weatherData}} Kelvin

    </div>



</body>
</html>
